exports.Yargs = function(processArgs = []) {
  const inst = {
    usage: () => inst,
    version: () => inst,
    alias: () => inst,
    options: () => inst,
    epilogue: () => inst,
    check: (fn) => { if (typeof fn === 'function') fn(inst.argv); return inst; },
    argv: { _: processArgs, $0: '' },
  };
  return inst;
};
exports.processArgv = {
  hideBin: (argv) => Array.isArray(argv) ? argv.slice(2) : []
};
